package aoc02;

public class BathroomSecurity {
	public static void main (String[] args) {
		
		String instructions = "\r\n" + 
				"\r\n" + 
				"\r\n" + 
				"\r\n" + 
				"";
		
		System.out.println(getBathroomCode(instructions));
		
	}
	
	public static String getBathroomCode (String instructions) {
		
		instructions=instructions.toUpperCase();
		String[] arrayOfInstructions = instructions.split("\n");
		Keypad bathroom = new Keypad();
		
		for (String arr : arrayOfInstructions) {
			for (int i=0;i<arr.length();i++) {
				switch(arr.charAt(i)) {
				case 'U': bathroom.up(); break;
				case 'D': bathroom.down(); break;
				case 'L': bathroom.left(); break;
				case 'R': bathroom.right(); break;
				default: break;
	
				}
				
			}
			bathroom.save();
		}
		
		return bathroom.getSecretCode();
	} 
}
